/**
 * 拖拽文件
 *
 * @param {String} [option.element] 是
 * @param {Function} [option.callback] 是
 * @param {Boolean} [option.queue] 可选 默认 false
 *
 * new FileDrop({
 *     element: "#app",
 *     callback(result) {
 *         console.log(result);
 *     }
 * })
 *
 * */
class FileDrop{constructor(e){null!==e&&"object"==typeof e&&0<Object.keys(e).length?(this.option=e,this.init()):console.error("The option does not appear to be an object")}init(){var e=document.getElementById(this.option.el);if(!e)return console.error("The element you entered could not be found"),!1;e.addEventListener("dragover",this.dragover.bind(this),!1),e.addEventListener("drop",this.drop.bind(this),!1)}dragover=e=>{e.preventDefault()};drop=e=>{e.preventDefault();var t=e.dataTransfer.files,e=e.dataTransfer.items;e?this.queue(e):this.queue(t)};queue=e=>{var t=this.option?.queue;try{var r=Array.from(e).map(e=>e.getAsFile());t?this.option.callback?.(r):r.forEach(e=>this.option.callback?.(e))}catch(e){console.error("An error occurred while processing the queue:",e)}}}