	/**
	 * 获取拖拽文件
	 *
	 * @param {String} [option.element] 是
	 * @param {Function} [option.callback] 是
	 *
	 * new FileUploader({
	 *     element: "#app",
	 *     callback(result) {
	 *         console.log(result);
	 *     }
	 * })
	 *
	 * */
class FileUploader{constructor(e){"object"==typeof e&&"{}"!=JSON.stringify(e)?(this.option=e,this.init()):console.error("not is object")}init(){var e=document.querySelector(this.option.element);e?(e.addEventListener("dragenter",this.dragenter),e.addEventListener("dragover",this.dragover),e.addEventListener("drop",e=>{e.preventDefault();var t=e.dataTransfer.files,e=e.dataTransfer.items;0<t.length?"function"==typeof this.option.callback&&this.option.callback(t):0<e.length?"function"==typeof this.option.callback&&this.option.callback(e):console.error("123")})):console.error("not find element")}dragover(e){e.preventDefault()}dragenter(e){e.preventDefault()}}